sp_widget.client_script:
function($scope,$rootScope) {
    /* widget controller */
    var c = this;

    $rootScope.$on('selectNote', function(event,data) {
      c.server.get({
        action: 'getNote',
        sys_id: data
      }).then(function(r) {
        $scope.data = r.data.note;
      });
    });

		$scope.updateNote = function(updateType) {
			c.server.get({
				action: 'updateNote',
				sys_id: $scope.data.sys_id,
				noteBody: $scope.data.note,
				noteTitle: $scope.data.title
			}).then(function(r) {
				$rootScope.$emit('changedNote', $scope.data);
			});
		}
  }

sp_widget.script:
(function() {
    /* populate the 'data' object */
    if (input && input.sys_id) {
      var note = new GlideRecord('x_snc_createnotes_note');
      if (note.get(input.sys_id)) {
        if (input.action == 'getNote') {
          data.note = {};
          $sp.getRecordValues(data.note, note, "title, note, sys_id");
        } else if (input.action == 'updateNote') { 
					note.title = input.noteTitle; 
					note.note = input.noteBody; 
					note.update(); 
				}
      }
    }
  })();
